#include "CoordinatorStreamer.h"
#include "ReplayStreamer.h"
#include "string"
#include "iostream"
#include "sc2api/sc2_api.h"

using namespace std;
using namespace sc2;

int main(int argc, char **argv)
{
	for(int i=1;i<=751;i++)
	{
		Streamer strm;
		strm.run(argc, argv);
		++strm.Replay_Count;
	}
	return 0;
}

Streamer::Streamer()
{
}

unsigned int Streamer::Replay_Count  = 1;
void Streamer::run(int argc, char **argv)
{
	//sc2::Coordinator::SetWindowSize(800, 600);
	//sc2::Coordinator::SetStepSize(15);
	if (!sc2::Coordinator::LoadSettings(argc, argv))
	{
		cout << "sc2::Coordinate LoadSetting failed!" << endl;
	}
	//This is you replay.SC2Replay path
	string Replay_Path = "I:/SC2Replay/ZPlayer (" + to_string(Replay_Count) + ").SC2Replay";

	//Setting data version and launchPID can speed up game loading
	//const string data_version = "760581629FC458A1937A05ED8388725B";
	//const string launchPID = "E:/StarCraft II/Versions/Base71061/SC2_x64.exe";                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
	//sc2::Coordinator::SetDataVersion(data_version);
	//sc2::Coordinator::SetProcessPath(launchPID);

	ReplayStreamer replay_obs;

		if (!sc2::Coordinator::SetReplayPath(Replay_Path))
		{
			cout << "Coordinator SetReplayPath "<<Replay_Path<<" failed!" << endl;
			cout << Replay_Count << endl;
		}
	
	sc2::Coordinator::AddReplayObserver(&replay_obs);
		while (sc2::Coordinator::Update());
		if (replay_obs.Control()->IsFinishedGame())
		{
			cout << Replay_Path<<" is finished!" << endl;
			Coordinator::LeaveGame();
		}
}